func main() {
	s, t := "ABAACBAB", "ABC"
	fmt.Println(minWindow(s, t))

	s, t = "ADOBECODEBANC", "ABC"
	fmt.Println(minWindow(s, t))

	s, t = "a", "a"
	fmt.Println(minWindow(s, t))

	s, t = "a", "aa"
	fmt.Println(minWindow(s, t))

	s, t = "bbaa", "aba"
	fmt.Println(minWindow(s, t))

	s = ""
	t = ""
	fmt.Println(minWindow(s, t))
}
