/**
 * @param {number[]} arr
 * @param {number} target
 * @return {number}
 */
var threeSumMulti = function (arr, target) {
    const map = new Map();
    for (let i of arr) {
        if (map.get(i) == undefined) {
            map.set(i, 1)
        } else {
            map.set(i, map.get(i) + 1)
        }
    }

    const visited = new Map();
    let sum = 0;
    for (let i = 0; i < arr.length - 2; i++) {
        for (let j = i + 1; j < arr.length - 1; j++) {
            const lastVal = target - (arr[i] + arr[j])

            if (map.get(lastVal) != undefined) {
                const pair = new Array(arr[i], arr[j], lastVal).sort();
                if (!visited.get(`${pair[0]},${pair[1]},${pair[2]}`)) {
                    if (pair[0] == pair[1] && pair[1] == pair[2]) {
                        sum += calcCombi(arr.filter(x => x == pair[0]).length, 3)
                    } else if (pair[0] == pair[1]) {
                        sum += calcCombi(arr.filter(x => x == pair[0]).length, 2) * arr.filter(x => x == pair[2]).length
                    } else if (pair[1] == pair[2]) {
                        sum += arr.filter(x => x == pair[0]).length * calcCombi(arr.filter(x => x == pair[1]).length, 2)
                    } else {
                        sum += arr.filter(x => x == pair[0]).length * arr.filter(x => x == pair[1]).length * arr.filter(x => x == pair[2]).length;
                    }
                    visited.set(`${pair[0]},${pair[1]},${pair[2]}`, true);
                }
            }
        }
    }
    return sum;
};

var calcCombi = function (n, r) {
    
    let range = function(from, to) {
        var ar = [];
        for (var i = from; i <= to; i++) {
            ar.push(i);
        }
        return ar;
    }

    if (n < r) {
        return 0;
    }
    var a = range((n - r) + 1, n).reduce(function(x, y) { return x * y; });
    var b = range(1, r).reduce(function(x, y) { return x * y; });
    return a / b;
}

var threeSumMulti3 = function (arr, target) {
    MOD = 1_000_000_007;

    const count = new Array();
    let uniq = 0;
    for (let x of arr) {
        if (count[x] == undefined) {
            count[x]=0;
        }
        count[x]++;
        if (count[x] == 1) {
            uniq++;
        }
    }
    const keys = new Array();
    let t = 0;
    for (let i =0; i<= 100; i++) {
        if (count[i] > 0) {
            keys[t++] = i;
        }
    }
    let ans = 0;
    for (let i =0; i<keys.length; i++) {
        let x = keys[i];
        let T = target - x;
        let j = i;
        let k = keys.length-1;

        while (j <= k) {
            const y = keys[j];
            const z = keys[k];
            if (y+z < T) {
                j++;
            } else if (y + z > T) {
                k--;
            } else {
                if (i < j && j < k) {
                    ans += count[x] * count[y] * count[z];
                } else if (i == j && j < k) {
                    ans += count[x] * (count[x] - 1) / 2 * count[z];
                } else if (i < j && j == k) {
                    ans += count[x] * count[y] * (count[y] - 1) / 2;
                } else {  
                    ans += count[x] * (count[x] - 1) * (count[x] - 2) / 6;
                }
                ans %= MOD;
                j++;
                k--;
            }
        }
    }
    return ans;
}

var threeSumMulti2 = function (arr, target) {
    const map = new Map();
    for (let i of arr) {
        if (map.get(i) == undefined) {
            map.set(i, 1)
        } else {
            map.set(i, map.get(i) + 1)
        }
    }

    const visited = new Map();
    let sum = 0;
    for (let i = 0; i < arr.length - 2; i++) {
        for (let j = i + 1; j < arr.length - 1; j++) {
            for (let k= j + 1; k < arr.length; k++) {
                if (!visited.get(`${i},${j},${k}`) && target - (arr[i] + arr[j] + arr[k]) == 0) {
                    sum++;
                    visited.set(`${i},${j},${k}`, true);
                }
            }
        }
    }
    return sum;
};


